        const iconList = document.getElementById('icon-list');
        const iconSearch = document.getElementById('icon-search');
        const selectedIconInput = document.getElementById('selected-icon');

        function renderIcons(filter = '') {
            iconList.innerHTML = '';
            icons.filter(icon => icon.includes(filter)).forEach(icon => {
                const iconElement = document.createElement('div');
                iconElement.className = 'icon-item';
                iconElement.innerHTML = `<i class="${icon} fs-4"></i>`;
                iconElement.addEventListener('click', () => {
                    document.querySelectorAll('.icon-item').forEach(item => item.classList.remove('selected'));
                    iconElement.classList.add('selected');
                    selectedIconInput.value = icon;
                });
                iconList.appendChild(iconElement);
            });
        }

        iconSearch.addEventListener('input', (e) => {
            renderIcons(e.target.value);
        });

        renderIcons();
    });
